FilterToggle.raw_css:
.filter-toggle {
    display: inline-flex;
    align-items: center;
    padding: 6px 10px;
    font-weight: var(--fw-semibold);
    border-radius: var(--bdr-radius-small);
    border: none;
    box-shadow: inset 0 0px 0px 1px var(--bg-primary);
    background-color: var(--bg-pae);
    color: var(--fg-text-primary);
    cursor: pointer;
    white-space: nowrap;
    user-select: none;
    font-family: var(--font-family-main);
    font-size: var(--fs-350);
    text-transform: capitalize;
    transition: all 0.2s ease-in-out;
  }

  .filter-toggle:hover {
    box-shadow: inset 0 0px 0px 2px var(--bg-primary);
  }

  .filter-toggle.active {
    box-shadow: inset 0 0px 0px 4px var(--bg-primary);
    background-color: var(--bg-primary);
    color: var(--fg-text-inverse);
  }

  .filter-toggle.active:hover {
    box-shadow: inset 0 0px 0px 2px var(--bg-primary);
  }

FilterGroup.raw_css:
.filter-group {
    display: flex;
    flex-wrap: wrap;
    gap: 6px;
    padding-bottom: var(--space-xl);
  }

  @media (max-width: 640px) {
    .filter-group {
      overflow-x: auto;
      flex-wrap: wrap;
      padding-bottom: 1.5rem;
      scrollbar-width: none;
      -ms-overflow-style: none;
    }

    .filter-group::-webkit-scrollbar {
      display: none;
    }
  }

ChatInput.raw_css:
.input-container {
    padding: 1rem 0;
    width: 100%;
    max-width: 800px; /* Limit width for better readability */
  }

  .input-form {
    width: 100%;
  }

  .input-wrapper {
    display: flex;
    gap: 0.75rem;
    align-items: center;
    background: var(--bg-primary);
    border: 1px solid #e9ecef;
    border-radius: var(--bdr-radius-pill);
    padding: var(--space-base) var(--space-2xl);
    transition:
      border-color 0.2s ease,
      box-shadow 0.2s ease;
  }

  .input-wrapper:focus-within {
    outline: 2px solid var(--focus-ring-color, var(--bg-primary));
    outline-offset: 2px;
  }

  .message-input {
    flex: 1;
    border: none;
    outline: none;
    background: transparent;
    resize: none;
    /* Prevent zoom on input focus (iOS) - minimum 16px font size */
    font-size: var(--fs-350);
    line-height: 1.5;
    color: var(--fg-text-inverse);
    min-height: 24px;
    max-height: 120px;
    overflow-y: auto;
    font-family: var(--font-family-main);
  }

  .message-input::placeholder {
    color: var(--fg-text-inverse-60);
  }

  .message-input:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }

  .input-hint {
    padding-top: var(--space-md);
    text-align: center;
  }

  .hint-text {
    font-family: var(--font-family-alt);
    font-size: var(--fs-300);
    color: var(--fg-text-primary-60);
  }

  /* Responsive adjustments with iOS-specific fixes */
  @media (max-width: 768px) {
    .input-container {
      padding: 1rem;
      /* Account for safe areas on mobile */
      padding-left: max(1rem, env(safe-area-inset-left));
      padding-right: max(1rem, env(safe-area-inset-right));
      /* Critical: Add bottom padding to lift above browser chrome */
      padding-bottom: max(
        1rem,
        env(safe-area-inset-bottom),
        var(--keyboard-height, 0px)
      );
      /* Ensure input container is always visible */
      position: relative;
      z-index: 10;
    }

    .input-wrapper {
      padding: 0.625rem 0.875rem;
      /* Ensure minimum touch target */
      min-height: 44px;
    }

    .message-input {
      /* Maintain 16px minimum to prevent zoom */
      font-size: max(16px, 0.875rem);
    }

    .hint-text {
      font-size: 0.6875rem;
    }
  }

  /* Support for older browsers without env() */
  @supports not (padding: env(safe-area-inset-left)) {
    @media (max-width: 768px) {
      .input-container {
        padding-left: 1rem;
        padding-right: 1rem;
        padding-bottom: 1.5rem; /* Extra padding for iOS Safari */
      }
    }
  }

ChatMessage.raw_css:
/* Base message styles */
  .message {
    display: flex;
    gap: 0.75rem;
    padding: 0.75rem 1.5rem;
    align-items: flex-start;
    justify-content: flex-start;
  }

  /* User message specific styles */
  .user-message {
    flex-direction: row-reverse;
  }

  .user-content {
    display: flex;
    flex-direction: column;
    align-items: flex-end;
  }

  /* Message avatar styles */
  .message-avatar {
    display: flex;
    align-items: center;
    justify-content: center;
    flex-shrink: 0;
  }

  /* Message content styles */
  .message-content {
    flex: 1;
    max-width: calc(100% - 50px);
  }

  /* Message bubble styles */
  .message-bubble {
    background: var(--bg-primary);
    border-radius: 18px;
    padding: 0.75rem 1rem;
    max-width: 100%;
    word-wrap: break-word;
    position: relative;
  }

  .user-bubble {
    background: var(--bg-primary);
    color: var(--fg-text-inverse);
    border-radius: var(--bdr-radius-small) var(--bdr-radius-small) 0px
      var(--bdr-radius-small);
  }

  .assistant-bubble {
    background: var(--bg-inverse);
    color: var(--fg-text-primary);
    border-color: var(--fg-text-primary);
    border-style: solid;
    border-width: 1px;
    border-radius: var(--bdr-radius-small) var(--bdr-radius-small)
      var(--bdr-radius-small) 0px;
  }

  .message-bubble p {
    margin: 0;
    line-height: 130%;
    font-size: 1.2rem;
  }

  /* Message meta styles */
  .message-meta {
    margin-top: 0.25rem;
    padding: 0 0.25rem;
  }

  .message-time {
    font-size: 0.75rem;
    color: var(--fg-text-primary);
    opacity: 0.7;
  }

  .user-meta .message-time {
    color: var(--fg-text-primary);
  }

  /* Loading message styles */
  .loading-message {
    align-items: flex-start;
  }

  .loading-bubble {
    background: var(--bg-page);
    color: var(--fg-text-primary);
    border-radius: 18px;
    padding: 0.75rem 1rem;
    width: 100%;
    word-wrap: break-word;
    position: relative;
    box-sizing: border-box;
  }

  .loading-text {
    margin: 0;
    line-height: 130%;
    font-family: var(--font-family-alt);
    font-size: var(--fs-200);
  }

  /* Error message styles */
  .error-message {
    padding: 1rem 1.5rem;
  }

  .error-content {
    display: flex;
    gap: 0.75rem;
    align-items: flex-start;
    background: var(--error-bg, #fef2f2);
    border: 1px solid var(--error-border, #fecaca);
    border-radius: 8px;
    padding: 1rem;
  }

  .error-icon {
    flex-shrink: 0;
    font-size: 1.25rem;
  }

  .error-title {
    margin: 0 0 0.25rem 0;
    font-weight: 600;
    color: var(--error-text, #dc2626);
    font-size: 0.875rem;
  }

  .error-details {
    margin: 0;
    color: var(--error-text-secondary, #7f1d1d);
    font-size: 0.8125rem;
  }

  /* Welcome message styles */
  .prompt-buttons {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    gap: 0.5rem;
    margin-top: 1rem;
    max-width: 100%;
  }

  /* Responsive button layout */
  @media (min-width: 480px) {
    .prompt-buttons {
      gap: 0.75rem;
    }
  }

