ChatDialog.raw_css:
/* Mobile-first base styles */
  .chat-dialog {
    border: none;
    background: transparent;
    max-width: none;
    max-height: none;
    width: 100%;
    height: 100%;
    padding: 0;
    animation: fadeIn 0.4s ease-in-out;
  }

  .chat-dialog::backdrop {
    background: var(--bg-page-20);
    backdrop-filter: blur(4px);
    animation: backdropFadeIn 0.5s ease-in-out;
  }

  .chat-dialog[open] {
    display: flex;
    align-items: flex-end;
    justify-content: center;
  }

  /* Unified dialog content - consistent across all screen sizes */
  .dialog-content {
    background: var(--bg-page);
    width: 100vw;
    max-width: 100%;
    /* Reduced height for better mobile performance */
    height: 70vh;
    max-height: calc(
      100dvh - var(--keyboard-height, 0px) - env(safe-area-inset-bottom)
    );
    display: flex;
    flex-direction: column;
    overflow: hidden;
    padding-bottom: max(1rem, env(safe-area-inset-bottom));
    padding-inline: env(safe-area-inset-left) env(safe-area-inset-right);
    /* Top inline shadow */
    box-shadow: inset 0 1px 0 0 var(--fg-text-primary);
  }

  /* Enhanced Animations */
  @keyframes fadeIn {
    from {
      opacity: 0;
    }
    to {
      opacity: 1;
    }
  }

  @keyframes backdropFadeIn {
    from {
      opacity: 0;
      backdrop-filter: blur(0px);
    }
    to {
      opacity: 1;
      backdrop-filter: blur(4px);
    }
  }

  /* Desktop and larger screens - maintain consistent behavior */
  @media (min-width: 769px) {
    .dialog-content {
      /* Keep desktop height at 80vh */
      height: 80vh;
      padding-bottom: 0;
      padding-inline: 0;
    }
  }

