GridLayout.raw_css:
.grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-auto-rows: 1fr;
    gap: 0.5rem;
    grid-auto-flow: row dense;
    padding-top: var(--spc-700);
    padding-bottom: var(--spc-1000);
  }

  .grid-item {
    border-radius: var(--bdr-radius-small);
    overflow: hidden;
  }

  /* Mobile masonry - items span based on their size but in 2 columns */
  .grid-item.desktop-spans {
    grid-column: span min(var(--grid-cols), 2);
    grid-row: span var(--grid-rows);
  }

  /* Force square aspect ratio for 2-2 items */
  .grid-item[data-grid-size='2-2'] {
    aspect-ratio: 1 / 1;
  }

  /* Desktop breakpoint - 4+ columns */
  @media (min-width: 896px) {
    .grid {
      grid-template-columns: repeat(var(--columns), 1fr);
    }

    .grid-item.desktop-spans {
      grid-column: span var(--grid-cols);
      grid-row: span var(--grid-rows);
    }
  }

ProgressIndicator.raw_css:
.progress-container {
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: row;
    flex-direction: row;
    -webkit-align-items: center;
    align-items: center;
    -webkit-justify-content: center;
    justify-content: flex-start;
    gap: 0;
    width: 100%;
  }

  .indicator {
    display: -webkit-flex;
    display: flex;
    -webkit-align-items: center;
    align-items: center;
    background-color: rgb(var(--color-bg-page));
    color: var(--fg-text-muted, #666);
    opacity: 0.5;
    font-size: var(--fs-300);
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    transition-property: color, opacity;
    transition-duration: 0.3s;
    transition-timing-function: ease;
  }

  .indicator.active {
    color: var(--fg-text, #000);
    opacity: 1;
  }

  .bridge {
    display: flex;
    justify-content: center;
    align-items: center;
    flex: 0 0 16px; /* Don't grow, don't shrink, fixed width */
    height: 1px;
    background-color: rgb(var(--color-bdr-muted));
    transition-property: flex-basis, background-color;
    transition-duration: 0.3s;
    transition-timing-function: ease;
  }

  .bridge.active {
    flex: 1 0 auto; /* Grow to fill available space */
    background-color: rgb(var(--color-bdr-primary));
  }

  .indicator.animated,
  .bridge.animated {
    transition-property: all;
    transition-duration: 0.3s;
    transition-timing-function: ease;
  }

  @media (prefers-reduced-motion: reduce) {
    .indicator,
    .bridge {
      transition: none;
    }
  }

  .bracket-left,
  .bracket-right,
  .step-number {
    font-family: var(--font-family-alt);
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    font-weight: 400;
  }

  .step-number {
    padding: 0 0.2rem;
  }

