    border-radius: var(--bdr-radius-small);
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: var(--spc-200);
    background-color: rgba(var(--bg-inverse) / 0);
    min-width: 250px;
    box-shadow: inset 0 0 0 1px rgba(var(--bdr-primary) / 0);
  }

  .progress-content {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    gap: var(--spc-300);
  }

  .progress-dots {
    display: flex;
    flex-direction: row;
    align-items: center;
    gap: var(--spc-100);
    background-color: rgba(var(--bg-positive) / 0.8);
    padding: var(--spc-100);
    border-radius: var(--bdr-radius-pill);
  }

  .progress-dot {
    width: 16px;
    height: 8px;
    position: relative;
    border-radius: var(--bdr-radius-pill);
  }

  .dot-inner {
    width: 100%;
    height: 100%;
    border-radius: var(--bdr-radius-pill);
    background-color: rgba(var(--fg-positive) / 0.2);
    border: 1px solid rgba(var(--fg-positive) / 0.4);
    transition: all 0.3s ease-in-out;
    transform: scale(1);
  }

  /* Visited state - filled dot */
  .progress-dot.visited .dot-inner {
    background-color: rgba(var(--fg-positive) / 1);
    border-color: rgba(var(--fg-positive) / 1);
  }

  /* Current dialog - pulsing effect */
  .progress-dot.current .dot-inner {
    animation: pulse 1.5s ease-in-out infinite;
  }

  /* Animation when dot becomes visited */
  .progress-dot.visited.animated .dot-inner {
    animation: fillDot 0.4s ease-out forwards;
  }

  @keyframes fillDot {
    0% {
      transform: scale(0.8);
      opacity: 0.5;
    }
    50% {
      transform: scale(1.2);
    }
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }

  @keyframes pulse {
    0% {
      transform: scale(1);
      opacity: 1;
    }
    50% {
      transform: scale(1.1);
      opacity: 0.8;
    }
    100% {
      transform: scale(1);
      opacity: 1;
    }
  }

  /* Mobile adjustments */
  @media (max-width: 896px) {
    /*  */
  }

Avatar.raw_css:
.avatar {
    position: relative;
    display: flex;
    align-items: center;
    justify-content: center;
    background-color: rgba(var(--bg-page) / 0);
    border: none;
    border-radius: var(--bdr-radius-small);
    padding: 4px;
    box-shadow: inset 0 0 0 1px rgba(var(--fg-text-primary) / 1);
    container-type: inline-size;
    transition: var(--transition-fast);
    isolation: isolate; /* Create stacking context for blend modes */
  }

  .avatar-image {
    display: flex;
    width: 100%;
    height: 100%;
    background-size: cover;
    background-position: top;
    background-repeat: no-repeat;
    border-radius: var(--bdr-radius-tiny);
    transition: var(--transition-fast);
    position: relative;
    z-index: 1;
    will-change: transform, filter;
  }

  .glitch-active .avatar-image {
    will-change: transform, filter;
  }

  /* True RGB Channel Layers */
  .glitch-red,
  .glitch-green,
  .glitch-blue {
    position: absolute;
    top: 4px; /* Account for avatar padding */
    left: 4px;
    right: 4px;
    bottom: 4px;
    background-size: cover;
    background-position: top;
    background-repeat: no-repeat;
    border-radius: var(--bdr-radius-tiny);
    pointer-events: none;
    opacity: 0;
    will-change: transform, opacity;
    transform: translateZ(0); /* Hardware acceleration */
    backface-visibility: hidden;
  }

  /* RGB Channel color filtering for authentic separation */
  .glitch-red {
    filter: sepia(1) saturate(10) hue-rotate(-60deg) brightness(1.2)
      contrast(1.5);
    mix-blend-mode: screen;
    z-index: 3;
  }

  .glitch-green {
    filter: sepia(1) saturate(10) hue-rotate(60deg) brightness(1.1)
      contrast(1.3);
    mix-blend-mode: screen;
    z-index: 2;
  }

  .glitch-blue {
    filter: sepia(1) saturate(10) hue-rotate(180deg) brightness(1.3)
      contrast(1.4);
    mix-blend-mode: screen;
    z-index: 4;
  }

  /* Enhanced Scanline Overlay */
  .glitch-scanlines {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: repeating-linear-gradient(
      0deg,
      transparent,
      transparent 2px,
      rgba(255, 255, 255, 0.05) 2px,
      rgba(255, 255, 255, 0.05) 4px
    );
    border-radius: var(--bdr-radius-small);
    pointer-events: none;
    opacity: 0;
    z-index: 10;
    will-change: opacity;
    transform: translateZ(0);
  }

  /* Reduced motion fallback */
  @media (prefers-reduced-motion: reduce) {
    .glitch-red,
    .glitch-green,
    .glitch-blue,
    .glitch-scanlines {
      display: none !important;
    }

    .glitch-active .avatar-image {
      animation: glitch-pulse 2s ease-in-out infinite;
    }

    @keyframes glitch-pulse {
      0%,
      100% {
        opacity: 1;
      }
      50% {
        opacity: 0.8;
      }
    }
  }

  /* Size variants with consistent aspect ratio (4:5 from original) */
  .avatar-xs {
    width: clamp(24px, 10vw, 80px);
    aspect-ratio: 4 / 5;
  }

  .avatar-sm {
    width: clamp(40px, 15vw, 120px);
    aspect-ratio: 4 / 5;
  }

  .avatar-md {
    width: clamp(60px, 20vw, 180px); /* Current ChatTrigger size */
    aspect-ratio: 4 / 5;
  }

  .avatar-lg {
    width: clamp(80px, 25vw, 240px);
    aspect-ratio: 4 / 5;
  }

  /* Button-specific styles */
  .avatar-button {
    cursor: pointer;
  }

  .avatar-button:hover:not(.glitch-active) .avatar-image {
    transform: scale(0.98);
  }

  .avatar-button:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  .avatar-button:disabled:hover .avatar-image {
    transform: none;
  }

  /* Focus states using design system variables */
  .avatar-button:focus-visible {
    outline: var(--focus-ring-width) solid var(--focus-ring-color);
    outline-offset: var(--focus-ring-offset);
  }

  /* Hardware acceleration for all glitch elements */
  .glitch-active,
  .glitch-active .avatar-image,
  .glitch-red,
  .glitch-green,
  .glitch-blue,
  .glitch-scanlines {
    transform: translateZ(0);
    backface-visibility: hidden;
  }

