<script lang="ts">
	import LinkList from '../actions/LinkList.svelte';

  import { page } from '$app/stores'
  interface LinkItem{
    label:string;
    href:string
  }

interface LinkListArray{
  list?:LinkItem[];
}

let listData : LinkItem[] = [ 
  {label:"Index", href:"/"},
  {label:"Graphics", href:"/graphics"}
 ] 

let { list =listData } : LinkListArray = $props()

function getNavClasses(href: string): string {
  const isActive = href === '/' 
    ? $page.url.pathname === '/'
    : $page.url.pathname.startsWith(href);
    
  return `nav-link ${isActive ? 'active' : ''}`;
}

</script>



{#snippet LinkList()}
<ul>
  {#each list  as item }
    <a class={getNavClasses(item.href)} href={item.href}>{item.label}</a>
  {/each}
</ul>
{/snippet}

